var answers = ["<p class=\"result__answer--dec\">\u0426\u0432\u0435\u0442 \u043A\u043E\u0436\u0438 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u043D\u0435\u043E\u0434\u043D\u043E\u0440\u043E\u0434\u043D\u044B\u043C, \u043C\u043E\u0433\u0443\u0442 \u043F\u043E\u044F\u0432\u043B\u044F\u0442\u044C\u0441\u044F \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u0435 \u043E\u0442\u0435\u043A\u0438 \u0438 \u043F\u043E\u043A\u0440\u0430\u0441\u043D\u0435\u043D\u0438\u044F, \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E \u0432 \u0437\u043E\u043D\u0435 \u0431\u0451\u0434\u0435\u0440. \u041F\u0440\u0438 \u0441\u0436\u0430\u0442\u0438\u0438 \u043A\u043E\u0436\u0438 \u0431\u0443\u0434\u0435\u0442\n        \u0432\u0438\u0434\u043D\u0430 \u0442\u0430 \u0441\u0430\u043C\u0430\u044F \xAB\u0430\u043F\u0435\u043B\u044C\u0441\u0438\u043D\u043E\u0432\u0430\u044F \u043A\u043E\u0440\u043A\u0430\xBB. \u041A\u043E\u0436\u0430 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u0441\u0443\u0445\u043E\u0439 \u0438 \u043D\u0435\u0440\u043E\u0432\u043D\u043E\u0439. \u041D\u0430 \u044D\u0442\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u0432\u0441\u0451 \u043B\u0435\u0433\u043A\u043E \u0441\u043A\u043E\u0440\u0440\u0435\u043A\u0442\u0438\u0440\u043E\u0432\u0430\u0442\u044C, \u0435\u0441\u043B\u0438\n        \u043D\u0430\u0447\u0430\u0442\u044C \u0440\u0430\u0431\u043E\u0442\u0443 \u0441\u0440\u0430\u0437\u0443, \u043A\u0430\u043A \u0437\u0430\u043C\u0435\u0442\u0438\u0448\u044C \u0438\u0437\u043C\u0435\u043D\u0435\u043D\u0438\u044F!</p>", "<p class=\"result__answer--dec\">\u041D\u0430 \u044D\u0442\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u0443\u0436\u0435 \u043D\u0430\u0440\u0443\u0448\u0430\u0435\u0442\u0441\u044F \u043A\u0440\u043E\u0432\u043E\u043E\u0431\u0440\u0430\u0449\u0435\u043D\u0438\u0435, \u043E\u0442\u0442\u043E\u043A \u043B\u0438\u043C\u0444\u044B \u0437\u0430\u043C\u0435\u0434\u043B\u044F\u0435\u0442\u0441\u044F. \"\u0410\u043F\u0435\u043B\u044C\u0441\u0438\u043D\u043E\u0432\u043E\u0439 \u043A\u043E\u0440\u043A\u0438\" \u0435\u0449\u0435 \u043D\u0435\u0442, \u043D\u043E \u044D\u043B\u0430\u0441\u0442\u0438\u0447\u043D\u043E\u0441\u0442\u044C \u043A\u043E\u0436\u0438 \u0443\u0436\u0435 \u0441\u043D\u0438\u0436\u0430\u0435\u0442\u0441\u044F \u0438 \u043F\u043E\u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0430\u044F \u0441\u0443\u0445\u043E\u0441\u0442\u044C.<br>\n        \u0415\u0441\u043B\u0438 \u043D\u0435 \u043F\u0440\u0435\u0434\u043F\u0440\u0438\u043D\u044F\u0442\u044C \u043C\u0435\u0440\u044B, \u0432 \u0434\u0430\u043B\u044C\u043D\u0435\u0439\u0448\u0435\u043C \u043C\u043E\u0436\u0435\u0442 \u0440\u0430\u0437\u0432\u0438\u0442\u044C\u0441\u044F \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442, \u043D\u0430 \u043A\u043E\u0436\u0435 \u0431\u0443\u0434\u0443\u0442 \u0432\u0438\u0434\u043D\u044B \u0441\u0438\u043D\u044F\u043A\u0438 \u0434\u0430\u0436\u0435 \u043E\u0442 \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u0445 \u0443\u0434\u0430\u0440\u043E\u0432, \u043F\u043E\u044F\u0432\u0438\u0442\u0441\u044F \u0442\u0430 \u0441\u0430\u043C\u0430\u044F \"\u043A\u043E\u0440\u043A\u0430\". \u041D\u0430 \u044D\u0442\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u0432\u0441\u0451 \u043B\u0435\u0433\u043A\u043E \u0441\u043A\u043E\u0440\u0440\u0435\u043A\u0442\u0438\u0440\u043E\u0432\u0430\u0442\u044C, \u0435\u0441\u043B\u0438 \u043D\u0430\u0447\u0430\u0442\u044C \u0440\u0430\u0431\u043E\u0442\u0443 \u043F\u0440\u044F\u043C\u043E \u0441\u0435\u0439\u0447\u0430\u0441!</p>", "<p class=\"result__answer--dec\">\u0412\u0442\u043E\u0440\u0430\u044F \u0441\u0442\u0430\u0434\u0438\u044F \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430</p>\n        <p class=\"result__answer--dec\">\u0426\u0432\u0435\u0442 \u043A\u043E\u0436\u0438 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u043D\u0435\u043E\u0434\u043D\u043E\u0440\u043E\u0434\u043D\u044B\u043C, \u043C\u043E\u0433\u0443\u0442 \u043F\u043E\u044F\u0432\u043B\u044F\u0442\u044C\u0441\u044F \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u0435 \u043E\u0442\u0435\u043A\u0438 \u0438 \u043F\u043E\u043A\u0440\u0430\u0441\u043D\u0435\u043D\u0438\u044F, \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E \u0432 \u0437\u043E\u043D\u0435 \u0431\u0451\u0434\u0435\u0440. \u041F\u0440\u0438 \u0441\u0436\u0430\u0442\u0438\u0438 \u043A\u043E\u0436\u0438 \u0431\u0443\u0434\u0435\u0442 \u0432\u0438\u0434\u043D\u0430 \u0442\u0430 \u0441\u0430\u043C\u0430\u044F \xAB\u0430\u043F\u0435\u043B\u044C\u0441\u0438\u043D\u043E\u0432\u0430\u044F \u043A\u043E\u0440\u043A\u0430\xBB. \u041A\u043E\u0436\u0430 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u0441\u0443\u0445\u043E\u0439 \u0438 \u043D\u0435\u0440\u043E\u0432\u043D\u043E\u0439. \n        \u0418\u0437-\u0437\u0430 \u043E\u0442\u0442\u043E\u043A\u0430 \u043B\u0438\u043C\u0444\u044B, \u043C\u0438\u043A\u0440\u043E\u0446\u0438\u0440\u043A\u0443\u043B\u044F\u0446\u0438\u044F \u043A\u043E\u0436\u0438 \u0443\u0436\u0435 \u043D\u0430\u0440\u0443\u0448\u0435\u043D\u0430, \u043F\u043E\u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u043E\u0442\u0451\u0447\u043D\u043E\u0441\u0442\u044C.</p>\n        \n        <p class=\"result__answer--dec\">\u041F\u043E\u0441\u0442\u0435\u043F\u0435\u043D\u043D\u043E \u0436\u0438\u0440\u043E\u0432\u044B\u0435 \u043A\u043B\u0435\u0442\u043A\u0438 \u043E\u0431\u044A\u0435\u0434\u0438\u043D\u044F\u044E\u0442\u0441\u044F, \u043E\u0431\u0440\u0430\u0437\u0443\u044F \xAB\u0433\u0440\u043E\u0437\u0434\u044C\u044F\xBB. \u0426\u0435\u043B\u043B\u044E\u043B\u0438\u0442 \u0437\u0430\u043C\u0435\u0442\u0435\u043D \u043F\u0440\u0438 \u043B\u044E\u0431\u043E\u043C \u043E\u0441\u0432\u0435\u0449\u0435\u043D\u0438\u0438 \u0438 \u043F\u043E\u043B\u043E\u0436\u0435\u043D\u0438\u0438. \u041F\u0440\u0438 \u043F\u0430\u043B\u044C\u043F\u0430\u0446\u0438\u0438 (\u043F\u0440\u043E\u0449\u0443\u043F\u044B\u0432\u0430\u043D\u0438\u0438) \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u044F\u044E\u0442\u0441\u044F \u0441\u0440\u0435\u0434\u043D\u0438\u0435 \u0438 \u043C\u0435\u043B\u043A\u0438\u0435 \u0443\u0437\u0435\u043B\u043A\u0438. \u041F\u0440\u0438 \u0433\u043B\u0443\u0431\u043E\u043A\u043E\u043C \u043D\u0430\u0434\u0430\u0432\u043B\u0438\u0432\u0430\u043D\u0438\u0438 \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0431\u043E\u043B\u044C\u043D\u043E!</p>\n        \n        <p class=\"result__answer--dec>\u041D\u0430 \u0434\u0430\u043D\u043D\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E \u043F\u0440\u0435\u0434\u043F\u0440\u0438\u043D\u044F\u0442\u044C \u043C\u0435\u0440\u044B \u043F\u043E \u043B\u0435\u0447\u0435\u043D\u0438\u044E \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430 \u0438 \u0440\u0430\u0437\u0433\u043E\u043D\u0443 \u043B\u0438\u043C\u0444\u044B, \u0438\u043D\u0430\u0447\u0435 \u0432 \u0434\u0430\u043B\u044C\u043D\u0435\u0439\u0448\u0435\u043C \u043C\u043E\u0436\u0435\u0442 \u043F\u043E\u0442\u0440\u0435\u0431\u043E\u0432\u0430\u0442\u044C\u0441\u044F \u043C\u0435\u0434\u0438\u0446\u0438\u043D\u0441\u043A\u043E\u0435 \u0432\u043C\u0435\u0448\u0430\u0442\u0435\u043B\u044C\u0441\u0442\u0432\u043E.</p>", "<p class=\"result__answer--dec\">\u0422\u0440\u0435\u0442\u044C\u044F \u0441\u0442\u0430\u0434\u0438\u044F \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430</p>\n        <p class=\"result__answer--dec\">\u0426\u0435\u043B\u043B\u044E\u043B\u0438\u0442 \u043D\u0430 \u044D\u0442\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u043F\u0440\u043E\u0438\u0441\u0445\u043E\u0434\u0438\u0442 \u043E\u0442 \u0442\u043E\u0433\u043E, \u0447\u0442\u043E \u0436\u0438\u0440\u043E\u0432\u044B\u0435 \u043A\u043B\u0435\u0442\u043A\u0438 \u043E\u0431\u044A\u0435\u0434\u0438\u043D\u044F\u044E\u0442\u0441\u044F, \u043E\u0431\u0440\u0430\u0437\u0443\u044F \xAB\u0433\u0440\u043E\u0437\u0434\u044C\u044F\xBB. \u041C\u0438\u043A\u0440\u043E\u0446\u0438\u0440\u043A\u0443\u043B\u044F\u0446\u0438\u044F \u043A\u043E\u0436\u0438 \u043D\u0430\u0440\u0443\u0448\u0435\u043D\u0430, \u043F\u043E\u044D\u0442\u043E\u043C\u0443 \u043E\u0442\u0442\u043E\u043A \u043B\u0438\u043C\u0444\u044B \u0437\u0430\u043C\u0435\u0434\u043B\u044F\u0435\u0442\u0441\u044F \u0438 \u043F\u043E\u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u0432\u044B\u0440\u0430\u0436\u0435\u043D\u043D\u0430\u044F \u043E\u0442\u0451\u0447\u043D\u043E\u0441\u0442\u044C, \u041A\u043E\u0436\u0430 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0430\u0435\u0442 \u044D\u0444\u0444\u0435\u043A\u0442 \xAB\u0430\u043F\u0435\u043B\u044C\u0441\u0438\u043D\u043E\u0432\u043E\u0439 \u043A\u043E\u0436\u0443\u0440\u044B\xBB, \u043A\u043E\u0442\u043E\u0440\u0443\u044E \u0437\u0430\u043C\u0435\u0442\u043D\u043E \u043F\u0440\u0438 \u043B\u044E\u0431\u043E\u043C \u043E\u0441\u0432\u0435\u0449\u0435\u043D\u0438\u0438 \u0438 \u043F\u043E\u043B\u043E\u0436\u0435\u043D\u0438\u0438. \u041F\u0440\u0438 \u043F\u0430\u043B\u044C\u043F\u0430\u0446\u0438\u0438 (\u043F\u0440\u043E\u0449\u0443\u043F\u044B\u0432\u0430\u043D\u0438\u0438) \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u044F\u044E\u0442\u0441\u044F \u0441\u0440\u0435\u0434\u043D\u0438\u0435 \u0438 \u043C\u0435\u043B\u043A\u0438\u0435 \u0443\u0437\u0435\u043B\u043A\u0438.\n        \u0415\u0441\u043B\u0438 \u043D\u0435 \u043F\u0440\u0435\u0434\u043E\u0442\u0432\u0440\u0430\u0442\u0438\u0442\u044C \u043E\u0431\u0440\u0430\u0437\u043E\u0432\u0430\u043D\u0438\u0435 \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430 \u043D\u0430 \u0434\u0430\u043D\u043D\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438, \u043E\u043D \u043C\u043E\u0436\u0435\u0442 \u0440\u0430\u0437\u0432\u0438\u0442\u044C\u0441\u044F \u0432 4 \u0441\u0442\u0430\u0434\u0438\u044E.</p>\n        <br>\n        <p class=\"result__answer--dec\">\u0427\u0435\u0442\u0432\u0435\u0440\u0442\u0430\u044F \u0441\u0442\u0430\u0434\u0438\u044F \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430</p>\n        <p class=\"result__answer--dec\">\u042F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u0441\u0430\u043C\u043E\u0439 \u0442\u044F\u0436\u0435\u043B\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0435\u0439 \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u0430. \u0421\u0438\u043C\u043F\u0442\u043E\u043C\u044B: \u043E\u0442\u0451\u0447\u043D\u043E\u0441\u0442\u044C, \u0437\u0430\u0441\u0442\u043E\u0439 \u043B\u0438\u043C\u0444\u044B, \u043D\u0430\u0440\u0443\u0448\u0435\u043D\u0438\u044F \u043A\u0440\u043E\u0432\u043E\u043E\u0431\u0440\u0430\u0449\u0435\u043D\u0438\u044F. \u041D\u0430 \u044D\u0442\u043E\u0439 \u0441\u0442\u0430\u0434\u0438\u0438 \u0444\u043E\u0440\u043C\u0438\u0440\u0443\u044E\u0442\u0441\u044F \u043E\u0442\u0435\u043A\u0438, \u0440\u0443\u0431\u0446\u044B, \u0443\u0437\u043B\u044B \u0441\u0442\u0430\u043D\u043E\u0432\u044F\u0442\u0441\u044F \u043A\u0440\u0443\u043F\u043D\u044B\u043C\u0438 \u0438 \u0431\u043E\u043B\u0435\u0437\u043D\u0435\u043D\u043D\u044B\u043C\u0438. \u0412 \u0443\u0447\u0430\u0441\u0442\u043A\u0430\u0445 \u043A\u043E\u0436\u0438 \u0441 \u0446\u0435\u043B\u043B\u044E\u043B\u0438\u0442\u043E\u043C \u043F\u043E\u0432\u044B\u0448\u0430\u0435\u0442\u0441\u044F \u0442\u0435\u043C\u043F\u0435\u0440\u0430\u0442\u0443\u0440\u0430 \u0438 \u043E\u043D\u0430 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u0433\u043E\u0440\u044F\u0447\u0435\u0439. \u041A\u043E\u0436\u0430 \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u0442\u0430\u0435\u0442 \u0441\u0438\u043D\u044E\u0448\u043D\u044B\u0439 \u043E\u0442\u0442\u0435\u043D\u043E\u043A. \u041A \u0441\u043E\u0436\u0430\u043B\u0435\u043D\u0438\u044E, \u044D\u0442\u0430 \u0441\u0442\u0430\u0434\u0438\u044F - \u0441\u0435\u0440\u044C\u0451\u0437\u043D\u043E\u0435 \u043F\u0430\u0442\u043E\u043B\u043E\u0433\u0438\u0447\u0435\u0441\u043A\u043E\u0435 \u0441\u043E\u0441\u0442\u043E\u044F\u043D\u0438\u0435. \u041E\u043D\u043E \u0442\u0440\u0435\u0431\u0443\u0435\u0442 \u043C\u0435\u0434\u0438\u0446\u0438\u043D\u0441\u043A\u043E\u0433\u043E \u0432\u043C\u0435\u0448\u0430\u0442\u0435\u043B\u044C\u0441\u0442\u0432\u0430.</p>"];
var quiz = [{
  "question": "1/5<br> Количество видимых бугорков на коже",
  "option": ["Нет ни одной неровности на коже", "Бугорков на коже немного (не более четырех ямок в одной части тела – на ягодицах, бедрах, животе, руках, груди и пр.", "В одной части тела от 5 до 7 бугорков", "В одной части тела много бугорков (от 7)"]
}, {
  "question": "2/5<br> Глубина утолщений",
  "option": ["Кожа ровная", "Ямки едва различимые", "Ямки достаточно выраженные", "Ямки очень глубокие"]
}, {
  "question": "3/5<br> Частота расположения ямок на коже",
  "option": ["Кожа ровная", "На коже всего несколько ямок, расположенных вдали друг от друга", "Ямок на коже не много, но они расположены рядом", "Ямок на коже много и находятся они вплотную друг к другу"]
}, {
  "question": "4/5<br> Гладкость кожи",
  "option": ["Кожа гладкая, хорошо увлажненная", "Тонус кожи достаточно хороший, склонность к складкам незначительная", "Тонус кожи низкий, в некоторых участках провисает, в состоянии покоя на ней появляются складки", "Тонус кожи низкий, появляется эффект «холодца» (жировая часть сильно провисает), кожа склонна к появляению складок"]
}, {
  "question": "5/5<br> Тест «Щипка» (необходимо слегка сдавить большим и указательным пальцами кожу на бедрах, ягодицах, руках или животе",
  "option": ["Бугорков на сдавленной коже не возникло", "Появилось 1-2 небольшие ямки", "Появлось 3-5 небольших ямок", "Появились очень выраженные глубокие ямки (больше трех)"]
}];
var index = 0;
var dataAnswer = 0;
var score = 0;
var questions = quiz;
$(function () {
  // print question
  printQuestion(0);
}); // func to print question start

function printQuestion(i) {
  $(".question").html(questions[i].question);
  $(".answer__list label").eq(0).text(questions[i].option[0]);
  $(".answer__list label").eq(1).text(questions[i].option[1]);
  $(".answer__list label").eq(2).text(questions[i].option[2]);
  $(".answer__list label").eq(3).text(questions[i].option[3]);
} // *************
// func to check answer start 


function checkAnswer(option) {
  dataAnswer = $(option).data("opt");
} // *****************


$('.modal__bg0').toggleClass('toggle'); // func for the next question start

function showNext() {
  if (!$('.answer__item input').is(':checked')) {
    $(".answer__error").css("opacity", "1");
  } else {
    if (index == questions.length - 1) {
      showResult();
      return;
    }

    $('.answer__item input').prop('checked', false);
    $(".answer__error").css("opacity", "0");
    score += dataAnswer;
    index++;
    console.log(score);
    $('.modal__bg').removeClass('toggle');
    $('.modal__bg' + index).toggleClass('toggle');
    printQuestion(index);
  }
}

function showPrev() {
  if (index == 0) {
    return;
  }

  $('.answer__item input').prop('checked', false);
  $(".answer__error").css("opacity", "0");
  score -= dataAnswer;
  index--;
  $('.modal__bg').removeClass('toggle');
  $('.modal__bg' + index).toggleClass('toggle');
  printQuestion(index);
} // ********************
//function for result start


function showResult() {
  $("#questionScreen").hide();
  $("#resultScreen").show();

  if (score <= 1) {
    $('.modal__bg').removeClass('toggle');
    $('.modal__bg11').toggleClass('toggle');
    $(".result__text").text("Начальная стадия");
    $(".result__answer").html(answers[0]);
  } else if (score > 1 && score <= 5) {
    $('.modal__bg').removeClass('toggle');
    $('.modal__bg22').toggleClass('toggle');
    $(".result__text").text("Начало 2 стадии целлюлита");
    $(".result__answer").html(answers[1]);
  } else if (score > 5 && score <= 10) {
    $('.modal__bg').removeClass('toggle');
    $('.modal__bg33').toggleClass('toggle');
    $(".result__text").text("2 – начало 3 стадии целлюлита");
    $(".result__answer").html(answers[2]);
  } else if (score > 10 && score <= 15) {
    $('.modal__bg').removeClass('toggle');
    $('.modal__bg44').toggleClass('toggle');
    $(".result__text").text("3-4 стадия целлюлита");
    $(".result__answer").html(answers[3]);
  }
} // *********************
// function for take the test again


function takeTestAgain() {
  index = 0;
  dataAnswer = 0;
  score = 0;
  $("#resultScreen").hide();
  $("#questionScreen").show();
  $('.answer__item input').prop('checked', false);
  $('.modal__bg').removeClass('toggle');
  $('.modal__bg' + index).toggleClass('toggle');
  printQuestion(index);
}

