$(document).ready(function () {
  var r,
      a = 900;

  function e() {
    a -= 1;
    var e = Math.floor(a / 60),
        t = a - 60 * e;
    0 == e && 0 == t && clearInterval(r), t = 10 <= t ? t : "0" + t, $(".minutes").html(10 <= e ? e : "0" + e), $(".seconds").html(t);
  }

  r = setInterval(e, 1e3);
});